import { FcLike } from 'react-icons/fc';


function Card({key,course}){
    return (
        <div className='shadow-lg rounded-md bg-gray-600'>
            <div className=''>
                <img src={course.image.url} alt={course.image.alt} className='h-[220px]'></img>
                <div className="">
                    <button className='p-1 rounded-full bg-red-300 mx-2 hover:bg-green-600'>
                        <FcLike fontSize="1.75rem" />
                    </button>
                </div>
            </div>
            <div className='p-2'>
                <h2 className='font-bold text-xl'>{course.title}</h2>
                <p>{course.description.substring(0,100)}</p>
            </div>

        </div>
    )
}
export default Card;
